mapper.insert:
INSERT
  INTO
       KH_BOARD
       (
       BOARD_NO
     , BOARD_TYPE
     , CATEGORY_NO
     , BOARD_TITLE
     , BOARD_CONTENT
     , BOARD_WRITER
       )
VALUES
       (
	   SEQ_BNO.NEXTVAL
	 , 1
	 , #{category}
	 , #{boardTitle}
	 , #{boardContent}
	 , #{boardWriter}
       )


	SELECT SEQ_BNO.NEXTVAL FROM DUAL

